    const handleHover = () => {
      setIsHovering(true);
    };

    const handleMouseOut = () => {
      setIsHovering(false);
    };
  function changeIt(e){
    e.target.style.background = "url({gif})"
  }
  return (
      <div className="p" onMouseOver={changeIt}>
        <div className="navi">
          <div className="navi-circle"></div>
          <div className="navi-circle"></div>
          <div className="navi-circle"></div>
        </div>

        <a href={link} target='_blank' rel="noreferrer">
          <img src={img} alt="coming.." className="p-img" />
        </a>
      </div>
  )
}

export default Project
