import "./project.css"
function Project({img,link,gif}) {

  //   const [isHovering, setIsHovering] = useState(false);

  //   const handleHover = () => {
  //     setIsHovering(true);
  //   };

  //   const handleMouseOut = () => {
  //     setIsHovering(false);
  //   };
  // function changeIt(e){
  //   e.target.style.background = "url({gif})"
  // }

  function MouseOver(event) {
    event.target.style.background = "url({gif})";
  }
  function MouseOut(event){
    event.target.style.background="url(${img})";
  }
  return (
      <div className="p" >
        <div className="navi">
          <div className="navi-circle"></div>
          <div className="navi-circle"></div>
          <div className="navi-circle"></div>
        </div>

        <a href={link} target='_blank' rel="noreferrer">
          <img onMouseOver={MouseOver} onMouseOut={MouseOut} alt="coming.." className="p-img" />
        </a>
      </div>
  )
}

export default Project
